def convert_to_netscape(json_data):
    netscape_cookies = []
    for cookie in json_data["cookies"]:
        domain = cookie["domain"]
        domain_flag = "TRUE" if not cookie["hostOnly"] else "FALSE"
        path = cookie["path"]
        secure = "TRUE" if cookie["secure"] else "FALSE"
        expiration = int(cookie.get("expirationDate", 0))
        name = cookie["name"]
        value = cookie["value"]
        netscape_cookies.append(f"{domain}\t{domain_flag}\t{path}\t{secure}\t{expiration}\t{name}\t{value}")
    return "\n".join(netscape_cookies)

# Convert and save to a file
cookies_txt = convert_to_netscape(json_data)

# Write to file
with open("cookies.txt", "w") as file:
    file.write(cookies_txt)

print("Cookies converted and saved to cookies.txt")